import * as THREE from "https://esm.sh/three@0.177.0";
import { EffectComposer } from "https://esm.sh/three@0.177.0/examples/jsm/postprocessing/EffectComposer.js";
import { RenderPass } from "https://esm.sh/three@0.177.0/examples/jsm/postprocessing/RenderPass.js";
import { ShaderPass } from "https://esm.sh/three@0.177.0/examples/jsm/postprocessing/ShaderPass.js";

const GlassEffect = {
    scene: null,
    camera: null,
    renderer: null,
    composer: null,
    customPass: null,
    backgroundTexture: null,
    backgroundMesh: null,
    aspect: 1,
    backgroundScene: null,
    backgroundCamera: null,
    isActive: false,
    isInitialized: false,

    PARAMS: {
        animationSpeed: 0, // Zatrzymana animacja
    },

    init() {
        const canvas = document.getElementById("glassCanvas");
        if (!canvas) return;

        this.renderer = new THREE.WebGLRenderer({
            canvas,
            antialias: true,
            alpha: true,
            premultipliedAlpha: false,
        });
        this.renderer.setSize(window.innerWidth, window.innerHeight);
        this.renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));
        this.renderer.autoClear = false;

        this.aspect = window.innerWidth / window.innerHeight;

        this.backgroundScene = new THREE.Scene();
        this.backgroundCamera = new THREE.OrthographicCamera(
            -this.aspect,
            this.aspect,
            1,
            -1,
            0.1,
            10
        );
        this.backgroundCamera.position.z = 1;

        this.scene = new THREE.Scene();
        this.camera = new THREE.OrthographicCamera(
            -this.aspect,
            this.aspect,
            1,
            -1,
            0.1,
            10
        );
        this.camera.position.z = 1;

        this.createGradientBackground();
        this.setupPostProcessing();

        window.addEventListener("resize", () => this.onWindowResize());

        this.isInitialized = true;
        this.animate();
    },

    createGradientBackground() {
        // Tworzymy gradient odpowiadający tłu strony
        const canvas = document.createElement("canvas");
        canvas.width = 2048;
        canvas.height = 2048;
        const ctx = canvas.getContext("2d");
        const centerX = 1024;
        const centerY = 1024;

        // Sprawdzamy motyw
        const isDark =
            document.documentElement.getAttribute("theme") === "dark";

        // Gradient bazowy
        const gradient = ctx.createRadialGradient(
            centerX,
            centerY,
            0,
            centerX,
            centerY,
            1400
        );
        if (isDark) {
            gradient.addColorStop(0, "#1a1a1a");
            gradient.addColorStop(0.4, "#0d0d0d");
            gradient.addColorStop(0.8, "#000000");
            gradient.addColorStop(1, "#000000");
        } else {
            gradient.addColorStop(0, "#e4e4e452");
            gradient.addColorStop(0.45, "#cfcfcf62");
            gradient.addColorStop(0.9, "#b1b1b13f");
        }

        ctx.fillStyle = gradient;
        ctx.fillRect(0, 0, 2048, 2048);

        // === NOWE TŁO - CZYSTE I PROSTE ===

        // Dodajemy noise texture na koniec
        const imageData = ctx.getImageData(0, 0, 2048, 2048);
        const data = imageData.data;
        for (let i = 0; i < data.length; i += 4) {
            const noise = (Math.random() - 0.5) * 10;
            data[i] += noise; // R
            data[i + 1] += noise; // G
            data[i + 2] += noise; // B
        }
        ctx.putImageData(imageData, 0, 0);

        this.backgroundTexture = new THREE.CanvasTexture(canvas);
        this.createBackgroundMesh();
    },

    createBackgroundMesh() {
        if (this.backgroundMesh) {
            this.backgroundScene.remove(this.backgroundMesh);
        }

        const geometry = new THREE.PlaneGeometry(this.aspect * 2, 2);
        const material = new THREE.MeshBasicMaterial({
            map: this.backgroundTexture,
        });

        this.backgroundMesh = new THREE.Mesh(geometry, material);
        this.backgroundScene.add(this.backgroundMesh);
    },

    setupPostProcessing() {
        this.composer = new EffectComposer(this.renderer);
        const renderPass = new RenderPass(
            this.backgroundScene,
            this.backgroundCamera
        );
        this.composer.addPass(renderPass);
        this.setupDistortionPass();
    },

    setupDistortionPass() {
        const vertexShader = `
            varying vec2 vUv;
            void main() {
                vUv = uv;
                gl_Position = projectionMatrix * modelViewMatrix * vec4(position, 1.0);
            }
        `;

        const fragmentShader = `
            uniform sampler2D tDiffuse;
            varying vec2 vUv;

            void main() {
                // Bez żadnych efektów - czysty render
                gl_FragColor = texture2D(tDiffuse, vUv);
            }
        `;

        this.customPass = new ShaderPass({
            uniforms: {
                tDiffuse: { value: null },
                uTime: { value: 0 },
            },
            vertexShader: vertexShader,
            fragmentShader: fragmentShader,
        });

        this.customPass.renderToScreen = true;
        this.composer.addPass(this.customPass);
    },

    onWindowResize() {
        this.aspect = window.innerWidth / window.innerHeight;

        if (this.camera) {
            this.camera.left = -this.aspect;
            this.camera.right = this.aspect;
            this.camera.updateProjectionMatrix();
        }

        if (this.backgroundCamera) {
            this.backgroundCamera.left = -this.aspect;
            this.backgroundCamera.right = this.aspect;
            this.backgroundCamera.updateProjectionMatrix();
        }

        if (this.renderer) {
            this.renderer.setSize(window.innerWidth, window.innerHeight);
        }

        if (this.composer) {
            this.composer.setSize(window.innerWidth, window.innerHeight);
        }

        if (this.backgroundTexture) {
            this.createBackgroundMesh();
            // Renderuj ponownie po resize
            this.render();
        }
    },

    animate(time = 0) {
        // Renderuj tylko raz - bez ciągłej animacji
        if (!this.renderer) return;

        if (this.customPass) {
            this.customPass.uniforms.uTime.value = 0; // Zatrzymany czas
        }

        if (this.composer) {
            this.composer.render();
        } else {
            this.renderer.clear();
            this.renderer.render(this.backgroundScene, this.backgroundCamera);
        }

        // Usuń requestAnimationFrame - renderuj tylko jeden raz
    },

    updateTheme() {
        // Odświeża teksturę tła gdy motyw się zmieni
        if (this.isInitialized) {
            this.createGradientBackground();
            // Renderuj ponownie po zmianie motywu
            this.animate();
        }
    },

    updateGrid() {
        // Odświeża linie siatki gdy zmienią się elementy
        if (this.isInitialized) {
            this.createGradientBackground();
            this.animate();
        }
    },

    render() {
        // Pojedynczy render bez animacji
        if (!this.renderer) return;

        if (this.composer) {
            this.composer.render();
        } else {
            this.renderer.clear();
            this.renderer.render(this.backgroundScene, this.backgroundCamera);
        }
    },

    activate() {
        const canvas = document.getElementById("glassCanvas");
        const button = document.getElementById("glassToggle");

        if (!this.isInitialized) {
            this.init();
        }

        this.isActive = true;

        if (canvas) {
            canvas.classList.add("active");
        }

        if (button) {
            button.classList.add("active");
        }
    },

    deactivate() {
        const canvas = document.getElementById("glassCanvas");
        const button = document.getElementById("glassToggle");

        this.isActive = false;

        if (canvas) {
            canvas.classList.remove("active");
        }

        if (button) {
            button.classList.remove("active");
        }
    },

    toggle() {
        if (this.isActive) {
            this.deactivate();
        } else {
            this.activate();
        }
    },
};

// Export do window dla dostępu z innych skryptów
window.GlassEffect = GlassEffect;

// Czekamy na załadowanie DOM
if (document.readyState === "loading") {
    document.addEventListener("DOMContentLoaded", () => {
        const glassToggle = document.getElementById("glassToggle");
        if (glassToggle) {
            glassToggle.addEventListener("click", () => GlassEffect.toggle());
        }

        // Automatycznie włącz efekt przy starcie
        setTimeout(() => {
            GlassEffect.activate();
        }, 500);
    });
} else {
    const glassToggle = document.getElementById("glassToggle");
    if (glassToggle) {
        glassToggle.addEventListener("click", () => GlassEffect.toggle());
    }

    // Automatycznie włącz efekt przy starcie
    setTimeout(() => {
        GlassEffect.activate();
    }, 500);
}

export default GlassEffect;

