    right: 0;
    background-color: var(--panel-bg);
    border: 1px solid var(--border-color);
    border-top: none;
    max-height: 250px;
    overflow-y: auto;
    z-index: 1000;
    display: none;
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.3);
}

.select-options.show {
    display: block;
}

.option {
    background-color: var(--panel-bg);
    color: var(--text-muted);
    padding: 0.5rem 1rem;
    font-family: "JetBrains Mono", monospace;
    font-size: 0.85rem;
    font-weight: 500;
    border-left: 2px solid transparent;
    cursor: pointer;
    transition: all 0.15s ease;
    user-select: none;
    height: 34px;
    display: flex;
    position: relative;
    overflow: hidden;
}

.option i {
    font-size: 0.9rem;
    width: 16px;
    text-align: center;
    color: var(--highlight-color);
    margin-right: 0.8rem;
}

.option:hover {
    background-color: var(--card-bg);
    color: var(--text-color);
    border-left: 2px solid var(--highlight-color);
}

.option.selected {
    background-color: rgba(255, 115, 0, 0.1);
    color: var(--highlight-color);
    border-left-color: var(--highlight-color);
    font-weight: 700;
}

.option:last-child {
    border-bottom: none;
}

.toolbar .fas {
    font-size: 1rem;
}

/* ===== Scrollbar CSS ===== */
  /* Firefox */
  * {
    scrollbar-width: none;
    scrollbar-color: #8f54a0 #ffffff;
  }

  /* Chrome, Edge, and Safari */
  *::-webkit-scrollbar {
    width: 16px;
  }

  *::-webkit-scrollbar-track {
    background: #ffffff;
  }

  *::-webkit-scrollbar-thumb {
    background-color: #8f54a0;
    border-radius: 10px;
    border: 3px solid #ffffff;
  }

// Add these to your existing script section
document.addEventListener("DOMContentLoaded", function () {
    // Remove draggable attribute from all elements
    document.querySelectorAll('[draggable="true"]').forEach((el) => {
        el.removeAttribute("draggable");
    });

    // Prevent dragstart event
    document.addEventListener("dragstart", function (e) {
        e.preventDefault();
        return false;
    });

    // Prevent drop event
    document.addEventListener("drop", function (e) {
        e.preventDefault();
        return false;
    });

    // Prevent dragover event
    document.addEventListener("dragover", function (e) {
        e.preventDefault();
        return false;
    });
});

/* --- ZMIENNE MOTYWU --- */
:root {
    /* LIGHT MODE: Platinum / Clean Tech */
    --bg-color: #e0e5ec;
    --panel-bg: #e0e5ec;

    /* Cienie ogólne */
    --shadow-drop: 9px 9px 16px rgb(163, 177, 198, 0.6), -9px -9px 16px rgba(255, 255, 255, 0.8),
        inset 0px 0px 0px 1px rgba(255, 255, 255, 0.4);

    --shadow-inset: inset 6px 6px 10px rgb(163, 177, 198, 0.7), inset -6px -6px 10px rgba(255, 255, 255, 0.9);

    --screen-shadow: inset 3px 3px 10px rgba(0, 0, 0, 0.5), inset -2px -2px 5px rgba(255, 255, 255, 0.1),
        0 1px 0 rgba(255, 255, 255, 0.5);

    --bevel-light: rgba(255, 255, 255, 0.6);
    --bevel-dark: rgba(163, 177, 198, 0.5);

    --text-color: #4a5568;
    --text-muted: #94a3b8;

    --led-yellow: #d97706;
    --led-green: #059669;
    --led-red: #dc2626;
    --led-glow-green: 0 0 8px rgba(5, 150, 105, 0.4);

    --screen-bg: #121212;
    --screen-gradient: linear-gradient(180deg, #2a2a2a 0%, #0a0a0a 100%);
    --screen-border: #8d99a6;

    --metal-gradient: linear-gradient(145deg, #f3f4f6 0%, #d1d5db 100%);

    /* --- POKRĘTŁA (LIGHT) --- */
    /* Jasny, szczotkowany metal */
    --knob-gradient: repeating-conic-gradient(#fff 0deg,
        #dff5ff 25deg,
        #a0b6cc 55deg,
        #7d96af 75deg,
        #dff5ff 90deg,
        #7d96af 105deg,
        #a0b6cc 125deg,
        #dff5ff 155deg,
        #fff 180deg);
    --knob-shadow: 0 -4px 4px -4px rgb(255 255 255 / 0.8), 0 4px 6px rgb(0 0 0 / 0.2),
        inset 0 1px 1px 1px rgb(255 255 255 / 0.6);
    --knob-border: rgba(125, 150, 175, 0.8);
    --knob-indicator: #333;
    /* Ciemny wskaźnik */

    --radius-main: 0px;
    --radius-inner: 0px;
}

:root[theme="dark"] {
    /* DARK MODE */
    --bg-color: #1a1a1a;
    --panel-bg: linear-gradient(180deg, #2a2a2a 0%, #1a1a1a 100%);

    --shadow-drop: 8px 8px 16px rgba(0, 0, 0, 0.6), -4px -4px 12px rgba(255, 255, 255, 0.05),
        inset 0px 1px 0px rgba(255, 255, 255, 0.1);

    --shadow-inset: inset 4px 4px 10px rgba(0, 0, 0, 0.8), inset -2px -2px 5px rgba(255, 255, 255, 0.05);

    --screen-shadow: inset 4px 4px 12px rgba(0, 0, 0, 0.9), inset -1px -1px 3px rgba(255, 255, 255, 0.05);

    --bevel-light: rgba(255, 255, 255, 0.08);
    --bevel-dark: rgba(0, 0, 0, 0.8);

    --text-color: #e2e8f0;
    --text-muted: #94a3b8;

    --screen-bg: #121212;
    --screen-gradient: radial-gradient(circle at 50% 30%, #252525 0%, #000000 100%);
    --screen-border: #111111;

    --metal-gradient: linear-gradient(135deg, #3f3f3f 0%, #2d3748 50%, #1a202c 100%);

    --led-yellow: #fbbf24;
    --led-green: #10b981;
    --led-red: #ef4444;
    --led-glow-green: 0 0 10px rgba(16, 185, 129, 0.6);

    /* --- POKRĘTŁA (DARK) --- */
    /* Ciemny, anodyzowany metal */
    --knob-gradient: repeating-conic-gradient(#555 0deg,
        #333 25deg,
        #222 55deg,
        #333 75deg,
        #444 90deg,
        #333 105deg,
        #222 125deg,
        #333 155deg,
        #555 180deg);
    --knob-shadow: 0 -2px 4px -2px rgba(255, 255, 255, 0.1), 0 4px 8px rgba(0, 0, 0, 0.6),
        inset 0 1px 1px 0px rgba(255, 255, 255, 0.1);
    --knob-border: rgba(0, 0, 0, 0.8);
    --knob-indicator: var(--led-yellow);
    /* Świecący wskaźnik w nocy */
}

/* RESET & BASE */
body {
    font-family: "Inter", sans-serif;
    background-color: var(--bg-color);
    color: var(--text-color);
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    transition: all 0.3s ease;
    background-image: url("data:image/svg+xml,%3Csvg viewBox='0 0 200 200' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='noiseFilter'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.8' numOctaves='3' stitchTiles='stitch'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23noiseFilter)' opacity='0.03'/%3E%3C/svg%3E");
    user-select: none;
}

*,
*::before,
*::after {
    box-sizing: border-box;
}

/* --- PANELE --- */
.panel-raised {
    background: var(--panel-bg);
    border-radius: var(--radius-main);
    box-shadow: var(--shadow-drop);
    position: relative;
    border-top: 1px solid var(--bevel-light);
    border-left: 1px solid var(--bevel-light);
    border-bottom: 1px solid var(--bevel-dark);
    border-right: 1px solid var(--bevel-dark);
}

/* EKRAN */
.screen-inset {
    background: var(--screen-bg);
    background-image: var(--screen-gradient);
    border-radius: var(--radius-inner);
    box-shadow: var(--screen-shadow);
    border: 2px solid var(--screen-border);
    position: relative;
    overflow: hidden;
}

.screen-inset::before {
    content: "";
    position: absolute;
    inset: 0;
    background-image: linear-gradient(rgba(255, 255, 255, 0.03) 1px, transparent 1px),
        linear-gradient(90deg, rgba(255, 255, 255, 0.03) 1px, transparent 1px);
    background-size: 4px 4px;
    opacity: 0.3;
    pointer-events: none;
    z-index: 1;
}

.glare-overlay {
    position: absolute;
    top: -50%;
    left: -50%;
    width: 200%;
    height: 200%;
    background: linear-gradient(to bottom right,
        rgba(255, 255, 255, 0.1) 0%,
        rgba(255, 255, 255, 0) 40%,
        rgba(255, 255, 255, 0) 100%);
    pointer-events: none;
    z-index: 2;
    transform: rotate(-15deg);
}

/* --- KOMPONENTY --- */
.screw {
    width: 12px;
    height: 12px;
    border-radius: 50%;
    background: var(--metal-gradient);
    box-shadow:
        1px 1px 3px rgba(0, 0, 0, 0.3),
        inset 1px 1px 1px rgba(255, 255, 255, 0.8);
    position: absolute;
    z-index: 10;
    display: flex;
    align-items: center;
    justify-content: center;
    border: 1px solid rgba(0, 0, 0, 0.1);
}

.screw-head {
    position: relative;
    width: 100%;
    height: 100%;
}

.screw-head::before,
.screw-head::after {
    content: "";
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    background: rgba(0, 0, 0, 0.4);
    box-shadow: inset 1px 1px 2px rgba(0, 0, 0, 0.2);
}

.screw-head::before {
    width: 60%;
    height: 2px;
    transform: translate(-50%, -50%) rotate(45deg);
}

.screw-head::after {
    width: 60%;
    height: 2px;
    transform: translate(-50%, -50%) rotate(-45deg);
}

/* --- POKRĘTŁA (Dostosowane do Dark Mode) --- */
.dial-housing {
    width: 90px;
    height: 90px;
    border-radius: 50%;
    background: var(--panel-bg);
    box-shadow: var(--shadow-drop);
    padding: 5px;
    display: flex;
    align-items: center;
    justify-content: center;
    cursor: ns-resize;
    transition: box-shadow 0.2s;
    position: relative;
    border: 1px solid var(--bevel-light);
}

.dial-housing:active {
    box-shadow: var(--shadow-inset);
}

.dial-housing::after {
    content: "";
    position: absolute;
    bottom: 5px;
    width: 4px;
    height: 4px;
    background: var(--text-muted);
    border-radius: 50%;
}

/* Styl samej gałki */
.dial-knob {
    width: 100%;
    height: 100%;
    border-radius: 50%;
    position: relative;
    z-index: 5;

    /* Użycie zmiennych zamiast stałych kolorów */
    background-image: var(--knob-gradient);
    box-shadow:
        var(--knob-shadow),
        inset 0 0 0 1px var(--knob-border);
    /* Metal rim */

    transition: transform 0.1s cubic-bezier(0.1, 0.57, 0.1, 1);
}

/* Wskaźnik na pokrętle */
.dial-knob::after {
    content: "";
    position: absolute;
    top: 8px;
    left: 50%;
    transform: translateX(-50%);
    width: 4px;
    height: 12px;
    background: var(--knob-indicator);
    /* Zmienia kolor w zależności od motywu */
    border-radius: 0px;
    box-shadow: 0 1px 2px rgba(0, 0, 0, 0.5);
}

/* Wgłębienie na palec */
.dial-knob::before {
    content: "";
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 60%;
    height: 60%;
    border-radius: 50%;
    background: linear-gradient(135deg, rgba(255, 255, 255, 0.2), rgba(0, 0, 0, 0.2));
    opacity: 0.3;
    pointer-events: none;
}

/* BUTTONS */
.phys-btn {
    background: var(--panel-bg);
    border-radius: 0px;
    box-shadow: var(--shadow-drop);
    color: var(--text-muted);
    font-family: "Orbitron", sans-serif;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.1s ease;
    display: flex;
    align-items: center;
    justify-content: center;
    position: relative;
    overflow: hidden;
    border: 1px solid rgba(255, 255, 255, 0.05);

}

.phys-btn i {
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 1.2rem;
    width: 50px;
    height: 50px;

    transition: all 0.2s ease;
}

.phys-btn:hover i {
    color: var(--led-green);
}

.phys-btn:active,
.phys-btn.active {
    box-shadow: var(--shadow-inset);
    transform: scale(0.96);
    color: var(--led-green);
    animation: press 0.2s ease-out forwards;
}

@keyframes press {
    0% {
        transform: scale(1);
    }

    50% {
        transform: scale(0.98);
    }

    100% {
        transform: scale(0.96);
    }
}

.phys-btn::before {
    content: "";
    position: absolute;
    inset: 0;
    background-image: url("data:image/svg+xml,%3Csvg viewBox='0 0 200 200' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='n'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='1.5' numOctaves='3' stitchTiles='stitch'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23n)' opacity='0.05'/%3E%3C/svg%3E");
    opacity: 0.5;
    pointer-events: none;
}

.switch-slot {
    background: var(--bg-color);
    box-shadow: var(--shadow-inset);
    border-radius: 0px;
    padding: 4px;
    height: 40px;
    display: flex;
    position: relative;
    cursor: pointer;
    width: 88px;
}

.switch-handle {
    height: 100%;
    width: 50%;
    background: var(--panel-bg);
    border-radius: 0px;
    box-shadow: 2px 2px 6px rgba(0, 0, 0, 0.3);
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 0.7rem;
    font-weight: bold;
    border: 1px solid rgba(255, 255, 255, 0.1);
    transition:
        transform 0.3s cubic-bezier(0.4, 0, 0.2, 1),
        color 0.3s;
}

.switch-on .switch-handle {
    transform: translateX(100%);
    color: var(--led-green);
    text-shadow: 0 0 5px var(--led-green);
}

.switch-off .switch-handle {
    transform: translateX(0);
    color: var(--text-muted);
}

.station-list-container {
    height: 180px;
    overflow-y: auto;
    padding: 10px;
    scrollbar-width: thin;
    scrollbar-color: var(--text-muted) transparent;
}

.station-list-container::-webkit-scrollbar {
    width: 6px;
}

.station-list-container::-webkit-scrollbar-track {
    background: transparent;
}

.station-list-container::-webkit-scrollbar-thumb {
    background-color: var(--text-muted);
    border-radius: 3px;
}

.station-item {
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 8px 12px;
    margin-bottom: 4px;
    border-radius: 0px;
    cursor: pointer;
    transition: all 0.2s;
    font-family: "Orbitron", sans-serif;
    font-size: 0.8rem;
    color: var(--text-muted);
}

.station-item:hover {
    background: rgba(255, 255, 255, 0.05);
    color: var(--text-color);
}

.station-item.active {
    background: rgba(255, 255, 255, 0.05);
    color: var(--led-yellow);
    text-shadow: 0 0 5px rgba(217, 119, 6, 0.3);
}

.station-icon {
    width: 24px;
    display: flex;
    justify-content: center;
    margin-right: 10px;
}

.station-led {
    width: 10px;
    height: 10px;
    border-radius: 50%;
    background: rgba(0, 0, 0, 0.2);
    box-shadow: inset 1px 1px 2px rgba(0, 0, 0, 0.5);
    transition:
        background 0.3s,
        box-shadow 0.3s;
}

.station-item.active .station-led {
    background: var(--led-green);
    box-shadow: 0 0 8px var(--led-green);
}

/* LAYOUT */
.main-container {
    display: flex;
    align-items: center;
    justify-content: center;
    min-height: 100vh;
    padding: 20px;
}

.grid-layout {
    display: grid;
    grid-template-columns: 320px 1fr;
    gap: 40px;
    width: 100%;
    max-width: 1200px;
}

.col-left,
.col-right {
    display: flex;
    flex-direction: column;
    gap: 30px;
}

.panel-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 20px;
    padding-bottom: 10px;
    border-bottom: 1px solid rgba(150, 150, 150, 0.15);
}

.brand-text {
    font-family: "Orbitron";
    font-weight: 700;
    letter-spacing: 2px;
    color: var(--text-muted);
    font-size: 0.8rem;
    text-shadow: 1px 1px 0px rgba(255, 255, 255, 0.2);
}

.scope-housing {
    width: 100%;
    height: 240px;
    border-radius: 0px;
    background: var(--panel-bg);
    box-shadow: var(--shadow-drop);
    padding: 12px;
    display: flex;
    align-items: center;
    justify-content: center;
    position: relative;
    border: 1px solid var(--bevel-light);
}

.scope-lens {
    width: 100%;
    height: 100%;
    border-radius: 0px;
    background: #000;
    position: relative;
    box-shadow: var(--screen-shadow);
    overflow: hidden;
    border: 2px solid var(--screen-border);
}

.scope-lens canvas {
    width: 100%;
    height: 100%;
    display: block;
}

.scope-lens::after {
    content: "";
    position: absolute;
    inset: 0;
    background: linear-gradient(135deg,
        rgba(255, 255, 255, 0.08) 0%,
        rgba(255, 255, 255, 0) 50%,
        rgba(0, 0, 0, 0.3) 100%);
    pointer-events: none;
    z-index: 10;
}

.mono-label {
    font-family: "Orbitron";
    font-size: 0.7rem;
    color: var(--text-muted);
    letter-spacing: 1px;
    margin-bottom: 4px;
    display: block;
}

.digital-readout {
    font-family: "Orbitron";
    color: var(--led-yellow);
    text-shadow: 0 0 8px var(--led-yellow);
    font-size: 1.2rem;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
    width: 100%;
    line-height: 1.5;
}

#theme-switch {
    width: 50px;
    height: 50px;
    border-radius: 0%;
    border: none;
    cursor: pointer;
    background: var(--panel-bg);
    box-shadow: var(--shadow-drop);
    color: var(--text-muted);
    display: flex;
    align-items: center;
    justify-content: center;
    transition: color 0.3s;
    border: 1px solid rgba(255, 255, 255, 0.1);
}

#theme-switch i {
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 1.2rem;
    width: 50px;
    height: 50px;
}

#theme-switch:hover {
    color: var(--led-yellow);
}

#theme-switch:active {
    box-shadow: var(--shadow-inset);
    transform: scale(0.95);
}

.vu-container {
    display: flex;
    flex-direction: column;
    gap: 4px;
    margin-top: auto;
    background: #111;
    padding: 6px;
    border-radius: 0px;
    box-shadow: inset 0 0 8px #000;
    border: 1px solid #333;
}

.vu-row {
    display: flex;
    gap: 3px;
    height: 8px;
    align-items: center;
}

.vu-label {
    font-family: "Orbitron";
    font-size: 0.5rem;
    color: var(--text-muted);
    width: 15px;
    text-align: right;
    margin-right: 4px;
}

.vu-segments {
    flex: 1;
    display: flex;
    gap: 2px;
    height: 100%;
}

.vu-seg {
    flex: 1;
    background: #222;
    border-radius: 0px;
    transition:
        background 0.05s ease-out,
        box-shadow 0.05s;
}

.vu-seg.on-green {
    background: var(--led-green);
    box-shadow: 0 0 4px var(--led-green);
}

.vu-seg.on-yellow {
    background: var(--led-yellow);
    box-shadow: 0 0 4px var(--led-yellow);
}

.vu-seg.on-red {
    background: var(--led-red);
    box-shadow: 0 0 4px var(--led-red);
}

.vu-seg.peak {
    background: #fff !important;
    box-shadow: 0 0 5px #fff !important;
}

@media (max-width: 1000px) {
    .grid-layout {
        grid-template-columns: 1fr;
    }

    .scope-lens {
        margin: 0 auto;
    }
}

