// --- URL SANITIZER (Naprawa linków) ---
function sanitizeUrl(url) {
    try {
        if (!url) return '';
        const u = new URL(url);

        // 1. Konwersja GitHub Blob -> Raw
        if (u.hostname === 'github.com' && u.pathname.includes('/blob/')) {
            u.hostname = 'raw.githubusercontent.com';
            u.pathname = u.pathname.replace('/blob/', '/');
        }

        // 2. Obsługa Gist (Naprawa błędu CORS)
        if (u.hostname === 'gist.github.com') {
            u.hostname = 'gist.githubusercontent.com';
        }

        return u.toString();
    } catch (e) {
        return url;
    }
}

// --- DATA FETCHING (ZAAWANSOWANE) ---
// Konfiguracja URL
const GITHUB_USER = 'skokivPr';
const GITHUB_REPO = 'json-lista';
const GITHUB_FILE = 'html.json';

const URL_MAIN = `https://raw.githubusercontent.com/${GITHUB_USER}/${GITHUB_REPO}/main/${GITHUB_FILE}`;
const URL_MASTER = `https://raw.githubusercontent.com/${GITHUB_USER}/${GITHUB_REPO}/master/${GITHUB_FILE}`;

function loadProjects() {
    console.log(`Próba pobrania danych z: ${URL_MAIN}`);

    fetch(URL_MAIN)
        .then(res => {
            if (res.ok) return res.json();

            console.warn(`Błąd pobierania z main (${res.status}). Próba z gałęzi master: ${URL_MASTER}`);
            return fetch(URL_MASTER).then(resMaster => {
                if (!resMaster.ok) {
                    throw new Error(`Nie znaleziono pliku w gałęziach main ani master (Status: ${resMaster.status})`);
                }
                return resMaster.json();
            });
        })
        .then(data => {
            console.log("Dane załadowane pomyślnie:", data);
            projects = data;
            renderProjects(projects);
        })
        .catch(error => {
            console.error("Krytyczny błąd ładowania:", error);
            grid.innerHTML = `
                        <div style="padding:4rem 2rem; text-align:center; color:var(--text-muted); grid-column:1/-1;">
                            <i data-lucide="alert-triangle" size="48" color="#ef4444" style="margin-bottom:1rem"></i><br>
                            SYSTEM ERROR: UNABLE TO FETCH DATA<br>
                            <span style="font-size:0.8em; opacity:0.7">${error.message}</span><br><br>
                            <div style="font-size:0.7em; color:var(--text-muted); background:var(--bg); padding:1rem; display:inline-block; border:1px solid var(--border-color);">
                                Sprawdź czy plik <strong>${GITHUB_FILE}</strong> istnieje w repozytorium <strong>${GITHUB_REPO}</strong><br>
                                Użytkownik: ${GITHUB_USER}
                            </div>
                        </div>
                    `;
            lucide.createIcons();
        });
}

// Uruchom ładowanie
loadProjects();

:root {
    --bg-color: #111;
    --panel-bg-color: #1a1a1a;
    --text-color: #ffffff;
    --secondary-text-color: #b0b0b0;
    --accent-color: #ff9800; /* A touch of orange for active states */
    --stripe-color: #2a2a2a;
    --border-color: #444;
    --font-primary: 'Orbitron', sans-serif;
    --font-secondary: 'Share Tech Mono', monospace;
}

* {
    box-sizing: border-box;
    margin: 0;
    padding: 0;
}

body {
    background-color: var(--bg-color);
    color: var(--text-color);
    font-family: var(--font-secondary);
    display: flex;
    justify-content: center;
    min-height: 100vh;
    padding: 1rem;
}

.container {
    width: 100%;
    max-width: 1200px;
    display: flex;
    flex-direction: column;
    gap: 1.5rem;
}

/* Header */
.site-header {
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;
    align-items: center;
    border-bottom: 1px solid var(--border-color);
    padding-bottom: 1rem;
    gap: 1rem;
}

.site-title {
    background-color: var(--text-color);
    color: var(--bg-color);
    padding: 5px 25px;
    clip-path: polygon(0 0, 100% 0, 90% 100%, 0% 100%);
}

.site-title h1 {
    font-family: var(--font-primary);
    font-size: clamp(1.5rem, 4vw, 2rem);
    font-weight: 900;
    letter-spacing: 2px;
}

/* Navigation */
.c-nav ul {
    list-style: none;
    display: flex;
    flex-wrap: wrap;
    gap: 1rem;
}

.c-nav a {
    color: var(--secondary-text-color);
    text-decoration: none;
    font-size: 0.9rem;
    letter-spacing: 1px;
    transition: all 0.2s ease-in-out;
}

.c-nav a:hover,
.c-nav a.active {
    color: var(--accent-color);
    text-shadow: 0 0 5px var(--accent-color);
}

/* Main Layout */
.main-layout {
    display: grid;
    grid-template-columns: 1fr;
    gap: 1.5rem;
    flex-grow: 1;
}

@media (min-width: 768px) {
    .main-layout {
        grid-template-columns: 2fr 1fr;
    }
}

main, aside {
    display: flex;
    flex-direction: column;
    gap: 1.5rem;
}

/* Component: Panel */
.c-panel {
    background-color: var(--panel-bg-color);
    border: 1px solid var(--border-color);
    padding: 1.5rem;
    position: relative;
}

.c-panel::before, .c-panel::after {
    content: '';
    position: absolute;
    width: 10px;
    height: 10px;
    border-color: var(--accent-color);
    border-style: solid;
}

.c-panel::before {
    top: -2px;
    left: -2px;
    border-width: 2px 0 0 2px;
}

.c-panel::after {
    bottom: -2px;
    right: -2px;
    border-width: 0 2px 2px 0;
}


.c-panel__header {
    display: flex;
    justify-content: space-between;
    align-items: baseline;
    border-bottom: 1px solid var(--border-color);
    padding-bottom: 0.75rem;
    margin-bottom: 1rem;
    letter-spacing: 1px;
    flex-wrap: wrap;
    gap: 0.5rem;
}

.c-panel__header h2, .c-panel__header h3 {
    font-family: var(--font-primary);
    font-size: 1.1rem;
    font-weight: 400;
    color: var(--text-color);
}

.c-panel__header-id {
    font-size: 0.8rem;
    color: var(--secondary-text-color);
}

.c-panel__content p {
    line-height: 1.6;
    margin-bottom: 1rem;
    color: var(--secondary-text-color);
}

/* Component: Button */
.c-btn {
    background: transparent;
    border: 1px solid var(--accent-color);
    color: var(--accent-color);
    padding: 10px 20px;
    font-family: var(--font-secondary);
    text-decoration: none;
    display: inline-block;
    letter-spacing: 2px;
    clip-path: polygon(0 0, 100% 0, 100% calc(100% - 10px), calc(100% - 10px) 100%, 0 100%);
    transition: all 0.2s ease-in-out;
    cursor: pointer;
    margin-top: 1rem;
}

.c-btn:hover {
    background-color: var(--accent-color);
    color: var(--bg-color);
    text-shadow: none;
}
.bracket-left { margin-right: 10px; }
.bracket-right { margin-left: 10px; }


/* Sidebar Widgets */
.data-box {
    border: 1px solid var(--border-color);
    padding: 10px;
    margin-bottom: 1rem;
}
.data-header {
    display: flex;
    justify-content: space-between;
    font-size: 0.7rem;
    color: var(--secondary-text-color);
    margin-bottom: 5px;
}
.data-value {
    text-align: right;
    font-size: 1.8rem;
    font-weight: bold;
    font-family: var(--font-primary);
    color: var(--accent-color);
}

.glitch-grid {
    width: 100%;
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(10px, 1fr));
    font-size: 0.8rem;
    color: var(--secondary-text-color);
    gap: 2px 5px;
    margin-bottom: 1rem;
}
.glitch-grid span {
    display: inline-block;
    min-width: 5px;
    text-align: center;
}

.info-cluster {
    width: 100%;
    position: relative;
}

.info-chart {
    width: 100%;
    max-width: 200px;
}

.chart-label {
    font-family: var(--font-secondary);
    fill: var(--secondary-text-color);
    font-size: 0.5rem;
}

.date-display {
    font-size: 1.2rem;
    letter-spacing: 2px;
    text-align: left;
    margin-top: 10px;
}


/* Footer */
.site-footer {
    margin-top: auto;
    display: flex;
    justify-content: space-between;
    align-items: center;
    border-top: 1px solid var(--border-color);
    padding-top: 1rem;
    font-size: 0.8rem;
}
.footer-left, .footer-right {
    background: var(--text-color);
    color: var(--bg-color);
    font-family: var(--font-primary);
    font-weight: 700;
    padding: 5px 20px;
}
.footer-left {
    clip-path: polygon(0 0, 100% 0, 90% 100%, 0% 100%);
}
.footer-right {
     clip-path: polygon(10% 0, 100% 0, 100% 100%, 0% 100%);
}

.footer-center {
    display: none;
}

@media (min-width: 768px) {
    .footer-center {
        display: flex;
        gap: 15px;
    }
}


.footer-center span {
    width: 20px;
    height: 5px;
    background-color: #555;
}

import * as THREE from "https://esm.sh/three@0.177.0";
import { EffectComposer } from "https://esm.sh/three@0.177.0/examples/jsm/postprocessing/EffectComposer.js";
import { RenderPass } from "https://esm.sh/three@0.177.0/examples/jsm/postprocessing/RenderPass.js";
import { ShaderPass } from "https://esm.sh/three@0.177.0/examples/jsm/postprocessing/ShaderPass.js";

const GlassEffect = {
    scene: null,
    camera: null,
    renderer: null,
    composer: null,
    customPass: null,
    backgroundTexture: null,
    backgroundMesh: null,
    aspect: 1,
    backgroundScene: null,
    backgroundCamera: null,
    isActive: false,
    isInitialized: false,

    PARAMS: {
        animationSpeed: 0, // Zatrzymana animacja
    },

    init() {
        const canvas = document.getElementById("glassCanvas");
        if (!canvas) return;

        this.renderer = new THREE.WebGLRenderer({
            canvas,
            antialias: true,
            alpha: true,
            premultipliedAlpha: false,
        });
        this.renderer.setSize(window.innerWidth, window.innerHeight);
        this.renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));
        this.renderer.autoClear = false;

        this.aspect = window.innerWidth / window.innerHeight;

        this.backgroundScene = new THREE.Scene();
        this.backgroundCamera = new THREE.OrthographicCamera(
            -this.aspect,
            this.aspect,
            1,
            -1,
            0.1,
            10
        );
        this.backgroundCamera.position.z = 1;

        this.scene = new THREE.Scene();
        this.camera = new THREE.OrthographicCamera(
            -this.aspect,
            this.aspect,
            1,
            -1,
            0.1,
            10
        );
        this.camera.position.z = 1;

        this.createGradientBackground();
        this.setupPostProcessing();

        window.addEventListener("resize", () => this.onWindowResize());

        this.isInitialized = true;
        this.animate();
    },

    createGradientBackground() {
        // Tworzymy gradient odpowiadający tłu strony
        const canvas = document.createElement("canvas");
        canvas.width = 2048;
        canvas.height = 2048;
        const ctx = canvas.getContext("2d");
        const centerX = 1024;
        const centerY = 1024;

        // Sprawdzamy motyw
        const isDark =
            document.documentElement.getAttribute("theme") === "dark";

        // Gradient bazowy
        const gradient = ctx.createRadialGradient(
            centerX,
            centerY,
            0,
            centerX,
            centerY,
            1400
        );
        if (isDark) {
            gradient.addColorStop(0, "#1a1a1a");
            gradient.addColorStop(0.4, "#0d0d0d");
            gradient.addColorStop(0.8, "#000000");
            gradient.addColorStop(1, "#000000");
        } else {
            gradient.addColorStop(0, "#e4e4e452");
            gradient.addColorStop(0.45, "#cfcfcf62");
            gradient.addColorStop(0.9, "#b1b1b13f");
        }

        ctx.fillStyle = gradient;
        ctx.fillRect(0, 0, 2048, 2048);

        // Funkcja rysująca sześciokąt
        const drawHexagon = (x, y, radius) => {
            ctx.beginPath();
            for (let i = 0; i < 6; i++) {
                const angle = (Math.PI / 3) * i;
                const hx = x + radius * Math.cos(angle);
                const hy = y + radius * Math.sin(angle);
                if (i === 0) {
                    ctx.moveTo(hx, hy);
                } else {
                    ctx.lineTo(hx, hy);
                }
            }
            ctx.closePath();
            ctx.stroke();
        };

        // Koncentryczne sześciokąty
        ctx.strokeStyle = isDark
            ? "rgba(255, 255, 255, 0.08)"
            : "rgba(0, 0, 0, 0.04)";
        ctx.lineWidth = 1.5;
        for (let r = 100; r < 1400; r += 150) {
            drawHexagon(centerX, centerY, r);
        }

        // Dodatkowe małe sześciokąty w rogach
        ctx.strokeStyle = isDark
            ? "rgba(255, 255, 255, 0.1)"
            : "rgba(0, 0, 0, 0.05)";
        ctx.lineWidth = 1;
        const corners = [
            [300, 300],
            [1748, 300],
            [300, 1748],
            [1748, 1748],
        ];
        corners.forEach(([cx, cy]) => {
            for (let r = 30; r < 120; r += 30) {
                drawHexagon(cx, cy, r);
            }
        });

        // Linie przekątne (jak na obrazku)
        ctx.strokeStyle = isDark
            ? "rgba(255, 255, 255, 0.1)"
            : "rgba(0, 0, 0, 0.05)";
        ctx.lineWidth = 1;

        // Pionowe linie z lewej
        for (let x = 100; x < 2048; x += 308) {
            ctx.beginPath();
            ctx.moveTo(x, 0);
            ctx.lineTo(x, 2048);
            ctx.stroke();
        }

        // Poziome linie
        for (let y = 100; y < 2048; y += 308) {
            ctx.beginPath();
            ctx.moveTo(0, y);
            ctx.lineTo(2048, y);
            ctx.stroke();
        }

        // Linie przekątne
        ctx.strokeStyle = isDark
            ? "rgba(255, 255, 255, 0.06)"
            : "rgba(0, 0, 0, 0.03)";
        for (let i = -2048; i < 4096; i += 409) {
            ctx.beginPath();
            ctx.moveTo(i, 0);
            ctx.lineTo(i + 2048, 2048);
            ctx.stroke();

            ctx.beginPath();
            ctx.moveTo(i, 2048);
            ctx.lineTo(i + 2048, 0);
            ctx.stroke();
        }

        // Gwiazdki w rogach
        const drawStar = (x, y, size) => {
            ctx.fillStyle = isDark
                ? "rgba(255, 255, 255, 0.6)"
                : "rgba(255, 255, 255, 0.8)";
            ctx.fillRect(x, y, size, size);
            ctx.beginPath();
            ctx.arc(x, y, size, 0, Math.PI * 2);
            ctx.fill();

            // Błysk krzyżowy
            ctx.strokeStyle = isDark
                ? "rgba(255, 255, 255, 0.4)"
                : "rgba(255, 255, 255, 0.6)";
            ctx.fillRect(x, y, size, size);
            ctx.lineWidth = 1;
            ctx.beginPath();
            ctx.moveTo(x - size * 3, y);
            ctx.lineTo(x + size * 3, y);
            ctx.stroke();
            ctx.beginPath();
            ctx.moveTo(x, y - size * 3);
            ctx.lineTo(x, y + size * 3);
            ctx.stroke();
        };

        // Gwiazdki w rogach
        drawStar(150, 150, 2);
        drawStar(1900, 150, 2);
        drawStar(150, 1900, 2);
        drawStar(1900, 1900, 2);

        // Losowe małe gwiazdki
        for (let i = 0; i < 30; i++) {
            const x = Math.random() * 2048;
            const y = Math.random() * 2048;
            const size = Math.random() * 1.5 + 0.5;
            drawStar(x, y, size);
        }

        // Dodatkowe detale - trójkąty i kwadraty
        const drawTriangle = (x, y, size, rotation = 0) => {
            ctx.save();
            ctx.translate(x, y);
            ctx.rotate(rotation);
            ctx.beginPath();
            ctx.moveTo(0, -size);
            ctx.lineTo(size * 0.866, size * 0.5);
            ctx.lineTo(-size * 0.866, size * 0.5);
            ctx.closePath();
            ctx.stroke();
            ctx.restore();
        };

        const drawSquare = (x, y, size, rotation = 0) => {
            ctx.save();
            ctx.translate(x, y);
            ctx.rotate(rotation);
            ctx.strokeRect(-size / 2, -size / 2, size, size);
            ctx.restore();
        };

        // Geometryczne ozdoby w rogach
        ctx.strokeStyle = isDark
            ? "rgba(255, 255, 255, 0.12)"
            : "rgba(0, 0, 0, 0.06)";
        ctx.lineWidth = 1.5;

        const decorPositions = [
            [200, 200],
            [1848, 200],
            [200, 1848],
            [1848, 1848],
        ];

        decorPositions.forEach(([x, y], index) => {
            drawTriangle(x, y, 40, (Math.PI / 4) * index);
            drawSquare(x, y, 60, (Math.PI / 8) * index);
            drawTriangle(x, y, 25, Math.PI + (Math.PI / 4) * index);
        });

        // Losowe geometryczne kształty na tłu
        ctx.strokeStyle = isDark
            ? "rgba(255, 255, 255, 0.05)"
            : "rgba(0, 0, 0, 0.03)";
        ctx.lineWidth = 1;

        for (let i = 0; i < 15; i++) {
            const x = Math.random() * 2048;
            const y = Math.random() * 2048;
            const size = Math.random() * 30 + 20;
            const shape = Math.floor(Math.random() * 3);
            const rotation = Math.random() * Math.PI;

            if (shape === 0) {
                drawHexagon(x, y, size);
            } else if (shape === 1) {
                drawTriangle(x, y, size, rotation);
            } else {
                drawSquare(x, y, size, rotation);
            }
        }

        // Dodajemy noise texture na koniec
        const imageData = ctx.getImageData(0, 0, 2048, 2048);
        const data = imageData.data;
        for (let i = 0; i < data.length; i += 4) {
            const noise = (Math.random() - 0.5) * 10;
            data[i] += noise; // R
            data[i + 1] += noise; // G
            data[i + 2] += noise; // B
        }
        ctx.putImageData(imageData, 0, 0);

        this.backgroundTexture = new THREE.CanvasTexture(canvas);
        this.createBackgroundMesh();
    },

    createBackgroundMesh() {
        if (this.backgroundMesh) {
            this.backgroundScene.remove(this.backgroundMesh);
        }

        const geometry = new THREE.PlaneGeometry(this.aspect * 2, 2);
        const material = new THREE.MeshBasicMaterial({
            map: this.backgroundTexture,
        });

        this.backgroundMesh = new THREE.Mesh(geometry, material);
        this.backgroundScene.add(this.backgroundMesh);
    },

    setupPostProcessing() {
        this.composer = new EffectComposer(this.renderer);
        const renderPass = new RenderPass(
            this.backgroundScene,
            this.backgroundCamera
        );
        this.composer.addPass(renderPass);
        this.setupDistortionPass();
    },

    setupDistortionPass() {
        const vertexShader = `
            varying vec2 vUv;
            void main() {
                vUv = uv;
                gl_Position = projectionMatrix * modelViewMatrix * vec4(position, 1.0);
            }
        `;

        const fragmentShader = `
            uniform sampler2D tDiffuse;
            varying vec2 vUv;

            void main() {
                // Bez żadnych efektów - czysty render
                gl_FragColor = texture2D(tDiffuse, vUv);
            }
        `;

        this.customPass = new ShaderPass({
            uniforms: {
                tDiffuse: { value: null },
                uTime: { value: 0 },
            },
            vertexShader: vertexShader,
            fragmentShader: fragmentShader,
        });

        this.customPass.renderToScreen = true;
        this.composer.addPass(this.customPass);
    },

    onWindowResize() {
        this.aspect = window.innerWidth / window.innerHeight;

        if (this.camera) {
            this.camera.left = -this.aspect;
            this.camera.right = this.aspect;
            this.camera.updateProjectionMatrix();
        }

        if (this.backgroundCamera) {
            this.backgroundCamera.left = -this.aspect;
            this.backgroundCamera.right = this.aspect;
            this.backgroundCamera.updateProjectionMatrix();
        }

        if (this.renderer) {
            this.renderer.setSize(window.innerWidth, window.innerHeight);
        }

        if (this.composer) {
            this.composer.setSize(window.innerWidth, window.innerHeight);
        }

        if (this.backgroundTexture) {
            this.createBackgroundMesh();
            // Renderuj ponownie po resize
            this.render();
        }
    },

    animate(time = 0) {
        // Renderuj tylko raz - bez ciągłej animacji
        if (!this.renderer) return;

        if (this.customPass) {
            this.customPass.uniforms.uTime.value = 0; // Zatrzymany czas
        }

        if (this.composer) {
            this.composer.render();
        } else {
            this.renderer.clear();
            this.renderer.render(this.backgroundScene, this.backgroundCamera);
        }

        // Usuń requestAnimationFrame - renderuj tylko jeden raz
    },

    updateTheme() {
        // Odświeża teksturę tła gdy motyw się zmieni
        if (this.isInitialized) {
            this.createGradientBackground();
            // Renderuj ponownie po zmianie motywu
            this.animate();
        }
    },

    render() {
        // Pojedynczy render bez animacji
        if (!this.renderer) return;

        if (this.composer) {
            this.composer.render();
        } else {
            this.renderer.clear();
            this.renderer.render(this.backgroundScene, this.backgroundCamera);
        }
    },

    activate() {
        const canvas = document.getElementById("glassCanvas");
        const button = document.getElementById("glassToggle");

        if (!this.isInitialized) {
            this.init();
        }

        this.isActive = true;

        if (canvas) {
            canvas.classList.add("active");
        }

        if (button) {
            button.classList.add("active");
        }
    },

    deactivate() {
        const canvas = document.getElementById("glassCanvas");
        const button = document.getElementById("glassToggle");

        this.isActive = false;

        if (canvas) {
            canvas.classList.remove("active");
        }

        if (button) {
            button.classList.remove("active");
        }
    },

    toggle() {
        if (this.isActive) {
            this.deactivate();
        } else {
            this.activate();
        }
    },
};

// Export do window dla dostępu z innych skryptów
window.GlassEffect = GlassEffect;

// Czekamy na załadowanie DOM
if (document.readyState === "loading") {
    document.addEventListener("DOMContentLoaded", () => {
        const glassToggle = document.getElementById("glassToggle");
        if (glassToggle) {
            glassToggle.addEventListener("click", () => GlassEffect.toggle());
        }

        // Automatycznie włącz efekt przy starcie
        setTimeout(() => {
            GlassEffect.activate();
        }, 500);
    });
} else {
    const glassToggle = document.getElementById("glassToggle");
    if (glassToggle) {
        glassToggle.addEventListener("click", () => GlassEffect.toggle());
    }

    // Automatycznie włącz efekt przy starcie
    setTimeout(() => {
        GlassEffect.activate();
    }, 500);
}

export default GlassEffect;
