/* monaco editor style */
.monaco-editor .bracket-highlighting-0,
.monaco-editor .bracket-highlighting-1,
.monaco-editor .bracket-highlighting-2 {
    color: #ce9178 !important;
}

.monaco-editor .marker-transparent {
    opacity: 0.25;
}

.monaco-editor .unexpected-closing-bracket {
    color: var(--editorText) !important;
}

.monaco-editor .line-numbers {
    font-family: "Consolas", "Courier New", monospace !important;
    user-select: none;
}

.monaco-editor .scroll-decoration {
    height: 10px !important;
    box-shadow: var(--color1) 0 6px 6px 0px inset !important;
    pointer-events: none;
}

.monaco-editor .minimap-shadow-visible {
    width: 10px !important;
    left: -10px !important;
    box-shadow: var(--color1) -6px 0 6px 0px inset !important;
    pointer-events: none;
}

:focus-visible {
    outline: var(--highlight) solid 1px;
    border-radius: 0px;
}

.monaco-action-bar.quick-input-inline-action-bar {
    display: none !important;
}

.monaco-editor .margin-view-overlays .codicon-folding-manual-collapsed,
.monaco-editor .margin-view-overlays .codicon-folding-manual-expanded,
.monaco-editor .margin-view-overlays .codicon-folding-expanded,
.monaco-editor .margin-view-overlays .codicon-folding-collapsed {
    transition: none !important;
}

button {
    font: inherit;
    background-color: #2a2a2a;
    border: 0;
    color: #e0e0e0;
    border-radius: 0;
    font-size: 1.35rem;
    padding: 0.375em 1em;
    font-weight: 600;
    text-shadow: 0 0.0625em 0 #000;
    box-shadow: inset 0 0.0625em 0 0 #3a3a3a, 0 0.0625em 0 0 #252525,
        0 0.125em 0 0 #202020, 0 0.25em 0 0 #1c1c1c, 0 0.3125em 0 0 #181818,
        0 0.375em 0 0 #141414, 0 0.425em 0 0 #101010, 0 0.425em 0.5em 0 #0a0a0a;
    transition: 0.15s ease;
    cursor: pointer;
}

button:active {
    translate: 0 0.225em;
    box-shadow: inset 0 0.03em 0 0 #3a3a3a, 0 0.03em 0 0 #252525,
        0 0.0625em 0 0 #202020, 0 0.125em 0 0 #1c1c1c, 0 0.125em 0 0 #181818,
        0 0.2em 0 0 #141414, 0 0.225em 0 0 #101010, 0 0.225em 0.375em 0 #0a0a0a;
}

:root {
            --bg: #f8f9fa;
            --panel-bg: #ffffff;
            --text-color: #212529;
            --text-muted: #6c757d;
            --highlight-color: #ff7300;
            --border-color: #dee2e6;
            --card-bg: #f8f9fa;
            --status-active-bg: #28a745;
        }

        :root[theme="dark"] {
            --bg: #121212;
            --panel-bg: #000000;
            --text-color: #e9ecef;
            --text-muted: #8d8d8d;
            --highlight-color: #ff7300;
            --border-color: #2a2a2a;
            --card-bg: #1a1a1a;
            --status-active-bg: var(--highlight-color);
        }

        * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
            font-family: "JetBrains Mono", "Share Tech Mono", monaco, courier;
        }

        body {
            background: var(--bg);
            color: var(--text-color);
            height: 100vh;
            width: 100vw;
            overflow: hidden;
        }

        .command-center-grid {
            display: grid;
            grid-template-columns: 1fr 1fr;
            grid-template-rows: 1fr 1fr;
            gap: 0.5rem;
            width: 100%;
            height: 100%;
            padding: 0.5rem;
        }

        .panel {
            background: var(--panel-bg);
            border: 1px solid var(--border-color);
            display: flex;
            flex-direction: column;
            overflow: hidden;
            position: relative;
            padding-top: 40px;
            /* Space for the header */
        }

        .panel-header {
            background: var(--highlight-color);
            color: #000;
            padding: 0.25rem 0.5rem;
            font-size: 0.65rem;
            font-weight: 700;
            text-transform: uppercase;
            position: absolute;
            top: 10px;
            left: 10px;
            height: 24px;
            line-height: 1.5;
            z-index: 10;
        }

        .panel-header-secondary {
            background: var(--panel-bg);
            color: var(--text-muted);
            border: 1px solid var(--border-color);
        }

        .panel-content {
            padding: 0.5rem;
            flex-grow: 1;
            overflow-y: auto;
            display: grid;
            grid-template-columns: 1fr 1fr;
            grid-template-rows: 1fr 1fr;
            gap: 0.5rem;
        }

        .info-card {
            background: var(--card-bg);
            border: 1px solid var(--border-color);
            padding: 0.5rem 1rem;
            display: flex;
            flex-direction: column;
            justify-content: space-between;
            text-decoration: none;
            color: var(--text-color);
            transition: background-color 0.2s, border-color 0.2s;
        }

        .info-card:hover {
            border-color: var(--highlight-color);
            background-color: rgba(255, 115, 0, 0.05);
        }

        .card-header {
            display: flex;
            justify-content: space-between;
            align-items: center;
            font-size: 0.7rem;
            margin-bottom: 0.25rem;
            color: var(--text-muted);
        }

        .card-status {
            font-weight: 700;
            font-size: 0.6rem;
            text-transform: uppercase;
        }

        .card-status.active {
            color: var(--status-active-bg);
        }

        .card-body {
            display: flex;
            align-items: flex-start;
            gap: 0.7rem;
            padding-top: 0.5rem;
        }

        .card-icon {
            font-size: 1.5rem;
            color: var(--text-muted);
            width: 20px;
            text-align: center;
        }

        .card-title-section {
            flex-grow: 1;
        }

        .card-title {
            font-weight: 700;
            font-size: 1.2rem;
            line-height: 1.2;
        }

        .card-subtitle {
            font-size: 0.75rem;
            color: var(--text-muted);
        }

        /* Carousel Styles */
        .carousel-panel {
            padding: 0;
            position: relative;
        }

        .carousel-slides {
            display: flex;
            height: 100%;
            width: 100%;
            transition: transform 0.5s ease-in-out;
        }

        .carousel-slide {
            flex-shrink: 0;
            width: 100%;
            height: 100%;
            background-size: cover;
            background-position: center;
            position: relative;
        }

        .visual-panel-overlay {
            background: linear-gradient(to top, rgba(0, 0, 0, 0.9), transparent);
            position: absolute;
            bottom: 0;
            left: 0;
            right: 0;
            padding: 1rem;
            color: #fff;
            width: 100%;
        }

        .visual-title {
            font-size: 1.25rem;
            font-weight: 700;
            color: var(--highlight-color);
        }

        .visual-subtitle {
            font-size: 0.9rem;
            opacity: 0.8;
            max-width: 80%;
        }

        .carousel-nav {
            position: absolute;
            bottom: 1rem;
            right: 1rem;
            z-index: 10;
        }

        .carousel-btn {
            background-color: rgba(0, 0, 0, 0.5);
            color: white;
            border: 1px solid var(--border-color);
            padding: 0.5rem;
            cursor: pointer;
            margin-left: 0.5rem;
        }

        .carousel-btn:hover {
            background-color: var(--highlight-color);
            color: #000;
        }


        .nav-controls {
            position: fixed;
            bottom: 20px;
            left: 20px;
            z-index: 1000;
        }

        .nav-btn {
            background: var(--highlight-color);
            color: #000;
            border: 1px solid var(--border-color);
            padding: 10px;
            width: 50px;
            height: 40px;
            cursor: pointer;
            font-family: inherit;
            font-size: 10px;
            font-weight: bold;
            transition: all 0.3s ease;
            text-transform: uppercase;
        }

        .nav-btn:hover {
            background: #e65c00;
            border-color: #e65c00;
        }

        .comm-panel-content {
            display: grid;
            grid-template-columns: 1fr 1fr;
            grid-template-rows: 1fr 1fr;
            gap: 0.5rem;
            flex-grow: 1;
            overflow-y: auto;
            padding: 0.5rem;
        }

        .comm-panel-content .info-card {
            grid-column: span 1;
        }

        .comm-panel-content .info-card:first-child {
            grid-row: span 2;
            justify-content: center;
        }

        .comm-panel-content .info-card.card-accent {
            background: var(--highlight-color);
            color: #000;
        }

        .comm-panel-content .info-card.card-accent .card-title,
        .comm-panel-content .info-card.card-accent .card-subtitle,
        .comm-panel-content .info-card.card-accent .card-icon,
        .comm-panel-content .info-card.card-accent .card-header {
            color: #000;
        }

/* --- Handsontable Theming --- */

/* Apply ht-theme-main only when light theme is active */
