    --ht-theme-main-background: var(--panel-bg);
    --ht-theme-main-color: var(--text-color);
    --ht-theme-main-border-color: var(--border-color);
    --ht-theme-main-header-background: var(--card-bg);
    --ht-theme-main-accent-color: var(--highlight-color);
    --ht-theme-main-active-border-color: var(--highlight-color);
}

