}

.station-item.active .station-led {
    background: var(--led-green);
    box-shadow: 0 0 8px var(--led-green);
}

/* LAYOUT */
.main-container {
    display: flex;
    align-items: center;
    justify-content: center;
    min-height: 100vh;
    padding: 20px;
}

.grid-layout {
    display: grid;
    grid-template-columns: 320px 1fr;
    gap: 40px;
    width: 100%;
    max-width: 1200px;
}

.col-left,
.col-right {
    display: flex;
    flex-direction: column;
    gap: 30px;
}

.panel-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 20px;
    padding-bottom: 10px;
    border-bottom: 1px solid rgba(150, 150, 150, 0.15);
}

.brand-text {
    font-family: "Orbitron";
    font-weight: 700;
    letter-spacing: 2px;
    color: var(--text-muted);
    font-size: 0.8rem;
    text-shadow: 1px 1px 0px rgba(255, 255, 255, 0.2);
}

.scope-housing {
    width: 100%;
    height: 240px;
    border-radius: 0px;
    background: var(--panel-bg);
    box-shadow: var(--shadow-drop);
    padding: 12px;
    display: flex;
    align-items: center;
    justify-content: center;
    position: relative;
    border: 1px solid var(--bevel-light);
}

.scope-lens {
    width: 100%;
    height: 100%;
    border-radius: 0px;
    background: #000;
    position: relative;
    box-shadow: var(--screen-shadow);
    overflow: hidden;
    border: 2px solid var(--screen-border);
}

.scope-lens canvas {
    width: 100%;
    height: 100%;
    display: block;
}

.scope-lens::after {
    content: "";
    position: absolute;
    inset: 0;
    background: linear-gradient(135deg,
        rgba(255, 255, 255, 0.08) 0%,
        rgba(255, 255, 255, 0) 50%,
        rgba(0, 0, 0, 0.3) 100%);
    pointer-events: none;
    z-index: 10;
}

.mono-label {
    font-family: "Orbitron";
    font-size: 0.7rem;
    color: var(--text-muted);
    letter-spacing: 1px;
    margin-bottom: 4px;
    display: block;
}

.digital-readout {
    font-family: "Orbitron";
    color: var(--led-yellow);
    text-shadow: 0 0 8px var(--led-yellow);
    font-size: 1.2rem;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
    width: 100%;
    line-height: 1.5;
}

#theme-switch {
    width: 50px;
    height: 50px;
    border-radius: 0%;
    border: none;
    cursor: pointer;
    background: var(--panel-bg);
    box-shadow: var(--shadow-drop);
    color: var(--text-muted);
    display: flex;
    align-items: center;
    justify-content: center;
    transition: color 0.3s;
    border: 1px solid rgba(255, 255, 255, 0.1);
}

#theme-switch i {
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 1.2rem;
    width: 50px;
    height: 50px;
}

#theme-switch:hover {
    color: var(--led-yellow);
}

#theme-switch:active {
    box-shadow: var(--shadow-inset);
    transform: scale(0.95);
}

.vu-container {
    display: flex;
    flex-direction: column;
    gap: 4px;
    margin-top: auto;
    background: #111;
    padding: 6px;
    border-radius: 0px;
    box-shadow: inset 0 0 8px #000;
    border: 1px solid #333;
}

.vu-row {
    display: flex;
    gap: 3px;
    height: 8px;
    align-items: center;
}

.vu-label {
    font-family: "Orbitron";
    font-size: 0.5rem;
    color: var(--text-muted);
    width: 15px;
    text-align: right;
    margin-right: 4px;
}

.vu-segments {
    flex: 1;
    display: flex;
    gap: 2px;
    height: 100%;
}

.vu-seg {
    flex: 1;
    background: #222;
    border-radius: 0px;
    transition:
        background 0.05s ease-out,
        box-shadow 0.05s;
}

.vu-seg.on-green {
    background: var(--led-green);
    box-shadow: 0 0 4px var(--led-green);
}

.vu-seg.on-yellow {
    background: var(--led-yellow);
    box-shadow: 0 0 4px var(--led-yellow);
}

.vu-seg.on-red {
    background: var(--led-red);
    box-shadow: 0 0 4px var(--led-red);
}

.vu-seg.peak {
    background: #fff !important;
    box-shadow: 0 0 5px #fff !important;
}

@media (max-width: 1000px) {
    .grid-layout {
        grid-template-columns: 1fr;
    }

    .scope-lens {
        margin: 0 auto;
    }
}
